import React from "react";
import ReactDOM from "react-dom/client";
import { combineReducers, createStore } from "redux";
import { v4 as uuidv4 } from 'uuid';

// Action Start
const addBlog = ({basliq,aciqlama}) => ({
  type: "ADD_BLOG",
  blog: {
    id:uuidv4(),
    title: basliq,
    desk: aciqlama,
  },
});

// Action END

// Reducer Start
const blogState = [];
const blogReducer = (state = blogState, action) => {
  switch (action.type) {
    case "ADD_BLOG":
      return [...state, action.blog];

    default:
      return state;
  }
};

// Reducer END

// Reducer Start
// const blogState1 = [];
// const blogReducer1 = (state = blogState1, action) => {
//   switch (action.type) {
//     case "ADD_BLOG":
//       return [...state, action.blog];

//     default:
//       return state;
//   }
// };

// Reducer END

// Store Start
const store = createStore(
  combineReducers({
    item: blogReducer,
    // itme2: blogReducer1,
  })
);
// Store END

store.subscribe(() => {
  console.log(store.getState());
});

store.dispatch(addBlog({basliq:"yeni basliq", aciqlama:"yeni aciqlama"}));

const App = () => {
  return <h1>Hello</h1>;
};

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);
