// Reducer Start
const blogState = [];
const blogReducer = (state = blogState, action) => {
  switch (action.type) {
    case "ADD_BLOG":
      return [...state, action.blog];
    case "REMOVE_BLOG":
      return state.filter(({ id }) => {
        return id != action.id;
      });
    case "UPDATE_BLOG":
        return state.map((blog)=>{
            if (blog.id == action.id) {
                return{
                    ...blog
                }
            }
        })
    default:
      return state;
  }
};

// Reducer END

